// http://feederbox.cc/gh/CupConverter (MIT)
function jpToUS(cupLetter, bustCm, waistCm, hipCm) {
  const metricToImp = (metric) => Math.round(metric * 0.393701)
  const lookupUSCup = (bustDiffIn) => bustDiffIn < 1 ? 'AA' : String.fromCharCode(Math.round(bustDiffIn) + 65 - 1)
  // 1 letter = +1cm increment
  // get ASCII code to increment letter, start at 1
  return `${metricToImp(bustCm)}${lookupUSCup(metricToImp(cupLetter.toUpperCase().charCodeAt() - 65 + 1))}-${metricToImp(waistCm)}-${metricToImp(hipCm)}`
}

module.exports = {
  jpToUS
}